mapper.select:
select xbpb.datasource_schema, xbpb.object_name, xbpb.description, (case when t_table_num.table_num is null then 0 else t_table_num.table_num end) table_num, (case when t_field_num.field_num is null then 0 else t_field_num.field_num end) field_num, (case when t_rel_table_num.rel_table_num is null then 0 else t_rel_table_num.rel_table_num end) rel_table_num, xbpb.plan_base_id, xbpb.plan_base_code, xbpb.plan_id, xbpb.datasource_type, xbpb.datasource_id, xbpb.tenant_id, xbpb.sql_type from xqua_batch_plan_base xbpb LEFT JOIN ( SELECT xbpt.plan_base_id, count(*) table_num FROM xqua_batch_plan_table xbpt GROUP BY xbpt.plan_base_id ) t_table_num ON t_table_num.plan_base_id = xbpb.plan_base_id LEFT JOIN ( SELECT xbpf.plan_base_id, count(*) field_num FROM xqua_batch_plan_field xbpf GROUP BY xbpf.plan_base_id ) t_field_num ON t_field_num.plan_base_id = xbpb.plan_base_id LEFT JOIN ( SELECT xbprt.plan_base_id, count(*) rel_table_num FROM xqua_batch_plan_rel_table xbprt GROUP BY xbprt.plan_base_id ) t_rel_table_num ON t_rel_table_num.plan_base_id = xbpb.plan_base_id where xbpb.tenant_id = #{tenantId}
and xbpb.project_id=#{projectId}
and xbpb.plan_id = #{planId}
and (case when t_table_num.table_num is null then 0 else t_table_num.table_num end) = #{tableNum}
and (case when t_field_num.field_num is null then 0 else t_field_num.field_num end) = #{fieldNum}
and (case when t_rel_table_num.rel_table_num is null then 0 else t_rel_table_num.rel_table_num end) = #{relTableNum}
and xbpb.datasource_schema like #{datasourceSchema}
and xbpb.object_name like #{objectName}
and xbpb.description like #{description}
order by xbpb.plan_base_id desc
