mapper.select:
select xbri.result_item_id, xbri.result_rule_id, xbri.plan_line_id, xbri.condition_id, xbri.check_way, xbri.check_item, xbri.count_type, xbri.field_name, xbri.check_field_name, xbri.where_condition, xbri.compare_way, xbri.regular_expression, xbri.lov_code, xbri.custom_sql, xbri.rel_datasource_type, xbri.rel_datasource_id, xbri.rel_schema, xbri.rel_table_name, xbri.relationship, xbri.warning_level_json, xbri.actual_value, xbri.wave_rate, xbri.current_value, xbri.exception_info, xbri.warning_level, xbri.tenant_id, xbrr.rule_type, xbrr.rule_code, xbrr.rule_name, xbrr.rule_desc, xbrr.check_type, xbrr.weight from xqua_batch_result_item xbri join xqua_batch_result_rule xbrr on xbri.result_rule_id = xbrr.result_rule_id join xqua_batch_result_base xbrb on xbrr.result_base_id = xbrb.result_base_id join xqua_batch_result xbr on xbrb.result_id = xbr.result_id
and xbr.result_id = #{resultId}
and xbrb.result_base_id = #{resultBaseId}
and xbri.tenant_id = #{tenantId}
and xbrr.rule_type = #{ruleType}
and xbrr.rule_name like #{ruleName}

select xbri.warning_level warningLevel, count(xbri.warning_level) countSum from xqua_batch_result_item xbri join xqua_batch_result_rule xbrr on xbri.result_rule_id = xbrr.result_rule_id
xbri.warning_level is not null
and xbri.tenant_id = #{tenantId}
and xbrr.result_base_id = #{resultBaseId}
GROUP BY xbri.warning_level
