mapper.select:
SELECT xbp.plan_id, xbp.plan_name, xbr.result_id, CASE WHEN xbr.plan_status IS NULL THEN '${@com.hand.hdsp.quality.infra.constant.PlanStatus@NOTACCESS}' ELSE xbr.plan_status END plan_status, xbr.mark, xbr.start_date, xbr.end_date, xbr.exception_info FROM xqua_batch_plan xbp LEFT JOIN ( SELECT * FROM xqua_batch_result WHERE result_id IN (SELECT MAX(result_id) FROM xqua_batch_result GROUP BY plan_id)) xbr ON xbr.plan_id = xbp.plan_id LEFT JOIN xqua_plan_group xpg ON xbp.group_id = xpg.group_id
xpg.group_id = #{groupId}
and xbp.tenant_id = #{tenantId}
and xbp.plan_name like #{planName}

select




from xqua_batch_plan xbp
LEFT JOIN xqua_batch_result xbr
on xbp.plan_id=xbr.plan_id


        xbr.tenant_id = #{tenantId}


        and xbr.plan_id = #{planId}


        and xbr.result_id = #{resultId}

SELECT xbr.result_id, xbr.plan_status, xbr.mark, xbr.start_date, xbr.end_date, xbp.plan_name, SUM( xbrb.rule_count ) rule_count, SUM( xbrb.exception_rule_count ) exception_rule_count FROM xqua_batch_result xbr JOIN xqua_batch_plan xbp ON xbp.plan_id = xbr.plan_id LEFT JOIN xqua_batch_result_base xbrb ON xbr.result_id = xbrb.result_id
and xbp.group_id = #{groupId}
and xbr.tenant_id = #{tenantId}
and xbp.plan_name like #{planName}
and xbr.plan_status = #{planStatus}
GROUP BY xbr.result_id, xbr.plan_status, xbr.mark, xbr.start_date, xbr.end_date, xbp.plan_name
