mapper.select:
select group_id id, concat('group',group_id) group_id, concat('group',parent_group_id) parent_group_id, parent_group_id parent_id, group_code, group_name, 'group' type from xqua_plan_group where group_type = 'BATCH' and tenant_id = #{tenantId} union select plan_id id, concat('plan', plan_id) group_id, concat('group',group_id) parent_group_id, group_id parent_id, plan_code group_code, plan_name group_name, 'plan' type from xqua_batch_plan xbp
and tenant_id = #{tenantId}
and xbp.plan_name like #{planName}

select group_id id, concat('group',group_id) group_id, concat('group',parent_group_id) parent_group_id, parent_group_id parent_id, group_code, group_name, 'group' type from xqua_plan_group where group_type = 'STREAMING' and tenant_id = #{tenantId} union select plan_id id, concat('plan', plan_id) group_id, concat('group',group_id) parent_group_id, group_id parent_id, plan_code group_code, plan_name group_name, 'plan' type from xqua_streaming_plan
and tenant_id = #{tenantId}
and xbp.plan_name like #{planName}
