mapper.select:
select * from ( select xsp.plan_id, xsp.plan_name, xsr.result_id, xsr.plan_status, xsr.mark, xsr.start_date, xsr.end_date, xsr.next_count_date from xqua_streaming_result xsr LEFT JOIN xqua_streaming_plan xsp on xsr.plan_id=xsp.plan_id LEFT JOIN xqua_plan_group xpg on xsp.group_id=xpg.group_id
xpg.group_id = #{groupId}
and xsr.tenant_id = #{tenantId}
and xsr.project_id = #{projectId}
and xsp.plan_name like #{planName}
and xsr.plan_status = #{planStatus}
union select xsp.plan_id, xsp.plan_name, null result_id, '${@com.hand.hdsp.quality.infra.constant.PlanStatus@NOTACCESS}' plan_status, null mark, xsp.creation_date start_date, null end_date, null next_count_date from xqua_batch_plan xsp LEFT JOIN xqua_plan_group xpg on xpg.group_id=xsp.group_id
xpg.group_id = #{groupId}
and xpg.tenant_id = #{tenantId}
and xsp.project_id = #{projectId}
and xsp.plan_name like #{planName}
) a where not exists (select 1 from ( select xsp.plan_id, xsp.plan_name, xsr.result_id, xsr.plan_status, xsr.mark, xsr.start_date, xsr.end_date, xsr.next_count_date from xqua_streaming_result xsr LEFT JOIN xqua_streaming_plan xsp on xsr.plan_id=xsp.plan_id LEFT JOIN xqua_plan_group xpg on xsp.group_id=xpg.group_id
xpg.group_id = #{groupId}
and xsr.tenant_id = #{tenantId}
and xsr.project_id = #{projectId}
and xsp.plan_name like #{planName}
and xsr.plan_status = #{planStatus}
union select xsp.plan_id, xsp.plan_name, null result_id, '${@com.hand.hdsp.quality.infra.constant.PlanStatus@NOTACCESS}' plan_status, null mark, xsp.creation_date start_date, null end_date, null next_count_date from xqua_batch_plan xsp LEFT JOIN xqua_plan_group xpg on xpg.group_id=xsp.group_id
xpg.group_id = #{groupId}
and xpg.tenant_id = #{tenantId}
and xsp.project_id = #{projectId}
and xsp.plan_name like #{planName}
) b where a.start_date < b.start_date and a.plan_id=b.plan_id)

select




from xqua_streaming_plan xsp
LEFT JOIN xqua_streaming_result xsr
on xsp.plan_id=xsr.plan_id


        xsr.tenant_id = #{tenantId}


        and xsp.project_id = #{projectId}


        and xsr.plan_id = #{planId}


        and xsr.result_id = #{resultId}

select
, xsrb.rule_count, xsrb.exception_rule_count from xqua_plan_group xpg left join xqua_streaming_plan xsp on xpg.group_id=xsp.group_id left join xqua_streaming_result xsr on xsp.plan_id=xsr.plan_id left join xqua_streaming_result_base xsrb on xsr.result_id=xsrb.result_id
xsr.plan_status != '${@com.hand.hdsp.quality.infra.constant.PlanStatus@NOTACCESS}'
and xpg.group_id = #{groupId}
and xsr.tenant_id = #{tenantId}
and xsr.project_id =#{projectId}
and xsp.plan_name like #{planName}
and xsr.plan_status = #{planStatus}

select ROUND((case when avg(xsr.mark) is null then 0 else avg(xsr.mark) end),1) mark,
(case when sum(xsrb.rule_count) is null then 0 else sum(xsrb.rule_count) end ) ruleCount,
(case when sum(xsrb.exception_rule_count) is null then 0 else sum(xsrb.exception_rule_count) end ) exceptionRuleCount
from xqua_streaming_result xsr
left join xqua_streaming_result_base xsrb
on xsr.result_id=xsrb.result_id


        xsr.tenant_id = #{tenantId}


        and xsr.project_id = #{projectId}


        and xsr.end_date >=  #{startDate}


        and xsr.end_date <=  #{endDate}

choose.when:
select to_char(xsr.end_date, 'yyyy-MM-dd') date,
(case when avg(xsr.mark) is null then 0 else avg(xsr.mark) end ) mark
from xqua_streaming_result xsr


        xsr.tenant_id = #{tenantId}


        and xsr.project_id = #{projectId}


        and xsr.end_date >=  #{startDate}


        and xsr.end_date <=  #{endDate}


group by to_char(xsr.end_date, 'yyyy-MM-dd')
order by to_char(xsr.end_date, 'yyyy-MM-dd') asc

choose.otherwise:
select date_format(xsr.end_date, '%Y-%m-%d') date,IFNULL(avg(xsr.mark),0) mark
from xqua_streaming_result xsr


        xsr.tenant_id = #{tenantId}


        and xsr.project_id = #{projectId}


        and xsr.end_date >=  #{startDate}


        and xsr.end_date <=  #{endDate}


group by date_format(xsr.end_date, '%Y-%m-%d')
order by date_format(xsr.end_date, '%Y-%m-%d') asc

choose.when:
select to_char(xsr.end_date, 'yyyy-MM-dd') date, xsrr.warning_level warningLevel, count(xsrr.warning_level) levelCount from xqua_streaming_result xsr INNER JOIN xqua_streaming_result_base xsrb on xsr.result_id=xsrb.result_id INNER JOIN xqua_streaming_result_rule xsrr on xsrb.result_base_id=xsrr.result_base_id
xsr.tenant_id = #{tenantId}
and xsr.project_id = #{projectId}
and xsr.end_date >= #{startDate}
and xsr.end_date <= #{endDate}
group by to_char(xsr.end_date, 'yyyy-MM-dd'),xsrr.warning_level order by to_char(xsr.end_date, 'yyyy-MM-dd') asc

choose.otherwise:
select date_format(xsr.end_date, '%Y-%m-%d') date, xsrr.warning_level warningLevel, count(xsrr.warning_level) levelCount from xqua_streaming_result xsr INNER JOIN xqua_streaming_result_base xsrb on xsr.result_id=xsrb.result_id INNER JOIN xqua_streaming_result_rule xsrr on xsrb.result_base_id=xsrr.result_base_id
xsr.tenant_id = #{tenantId}
and xsr.project_id = #{projectId}
and xsr.end_date >= #{startDate}
and xsr.end_date <= #{endDate}
group by date_format(xsr.end_date, '%Y-%m-%d'),xsrr.warning_level order by date_format(xsr.end_date, '%Y-%m-%d') asc

mapper.select:
select xsrr.topic_info topicInfo,
count(xsrr.topic_info) sumCount
from xqua_streaming_result xsr
LEFT JOIN xqua_streaming_result_base xsrb
on xsr.result_id=xsrb.result_id
LEFT JOIN xqua_streaming_result_rule xsrr
on xsrb.result_base_id=xsrr.result_base_id


        xsr.tenant_id = #{tenantId}


        and xsr.project_id = #{projectId}


        and xsr.end_date >=  #{startDate}


        and xsr.end_date <=  #{endDate}



        xsrr.topic_info like #{topicInfo}


GROUP BY xsrr.topic_info

choose.when:
select to_char(xsr.end_date, 'yyyy-MM-dd') date, xsrr.rule_type ruleType, (case when SUM(xsrb.exception_rule_count) is null then 0 else SUM(xsrb.exception_rule_count) end ) exceptionRuleCount from xqua_streaming_result xsr INNER JOIN xqua_streaming_result_base xsrb on xsr.result_id=xsrb.result_id INNER JOIN xqua_streaming_result_rule xsrr on xsrb.result_base_id=xsrr.result_base_id
xsr.tenant_id = #{tenantId}
and xsr.project_id = #{projectId}
and xsr.end_date >= #{startDate}
and xsr.end_date <= #{endDate}
group by to_char(xsr.end_date, 'yyyy-MM-dd'),xsrr.rule_type order by to_char(xsr.end_date, 'yyyy-MM-dd') asc

choose.otherwise:
select date_format(xsr.end_date, '%Y-%m-%d') date, xsrr.rule_type ruleType, IFNULL(SUM(xsrb.exception_rule_count),0) exceptionRuleCount from xqua_streaming_result xsr INNER JOIN xqua_streaming_result_base xsrb on xsr.result_id=xsrb.result_id INNER JOIN xqua_streaming_result_rule xsrr on xsrb.result_base_id=xsrr.result_base_id
xsr.tenant_id = #{tenantId}
and xsr.project_id = #{projectId}
and xsr.end_date >= #{startDate}
and xsr.end_date <= #{endDate}
group by date_format(xsr.end_date, '%Y-%m-%d'),xsrr.rule_type order by date_format(xsr.end_date, '%Y-%m-%d') asc
